import React from "react" 
import  ReactDOM  from "react-dom"
import  './modal.scss'


const ModalNewProd = ({ show,  title, children, add }) => {
    console.log('show', show);



    // handleLangChange: function () {
    //     var lang = this.refs.dropdown.value;
    //     this.props.onSelectLanguage(lang);            
    // },

    onTrigger = (e) => {
        this.props.parentCallback('modal close')
        e.t
    }

    return ReactDOM.createPortal(
        <>
           <div className={`modalContainer ${show ? "show" : "" }` }  onSelectLanguage={this.handleLanguage} ref={(ref) => this.dropdown = ref} >
               <div className="modal" onClick={(e)=> e.stopPropagation()}>
               <header className="modal_header">
                   <h2 className="modal_header-title"> {title}</h2>
                       <button className="close"  >X</button>
               </header>
               <main className="modal_content">{ children }</main>
               <footer className="modal_footer">

                   <button className="modal-close"   >Cancel</button>
                   <button className="submit" onClick={() => add()}>Add</button>
               </footer>
               </div>
           </div>  

        </>,
        document.getElementById("modal")
    );
};

export default ModalNewProd
