import React, { useState } from "react"
import ModalNewProd from "./Components/ModalNewProd";

const ListProducts = () => {

    const [products, setProducts] = useState([
        {
            id: 1,
            imageUrl: 'some url here',
            name: 'Product1 name',
            count: 4,
            size: {
                width: 200,
                height: 200
            },
            weight: '200g',
            comments: [Comment, Comment]
        },
        {
            id: 2,
            imageUrl: 'some url here',
            name: 'Product2 name',
            count: 6,
            size: {
                width: 200,
                height: 200
            },
            weight: '200g',
            comments: [Comment, Comment]
        }
    ])

    // const addProduct = () => products.concat()

    //modal 
    const [modal, setModal] = useState(false);
    const Toggle = () => setModal(!modal);

    // this.setState({
    //     modal: true
    // })


    // inputNewProd
    const [inputNewProd, setInputNewProd] =  useState('')
    const addProd = (e) => {
        console.log('addNewProd', event.target.value);
        setInputNewProd([
            ...products,
            {
                id: Math.random(),
                name: inputNewProd
            }
        ])
    }
    return (
        <div>
            <h1>ListProducts</h1>
            <ul products={products}>{products.map(product => (
                <li>
                    <span>id: {product.id}</span>,
                    <span>imageUrl: {product.imageUrl}</span>,
                    <span> name: {product.name}</span>,
                    <span> count: {product.count}</span>,
                    <span>size:  width: {product.size.width}, height: {product.size.height} </span>,
                    <span>  weight: {product.weight}</span>,
                    <span>  comments: {product.comments}</span>
                </li>
            ))}
            </ul>

            <button className='clickMe' onClick={() => Toggle()}>New Product</button>
            <ModalNewProd show={modal} close={Toggle}  add={addProd} title='Create new product'>

                {/* This is modal dynamic contant */}
                <form action="create-form">
                    <div>
                        <span>Add photo</span>
                        <input
                            className='input'
                            type="text"
                            text='name'
                            placeholder='url'
                        />
                    </div>
                    <div><span>Name</span>
                        <input
                            className='input'
                            type="text" text='name'
                            placeholder='name'
                        value={inputNewProd }
                        onChange={ e => setInputNewProd(e.target.value)}
                        />
                    </div>

                    <div><span>Count</span>
                        <input type="number" className='input' text='name' placeholder='count' /></div>

                    <div><span>Size</span>
                        <input type="number" className='input' text='name' placeholder='width' />
                        <input type="number" className='input' text='name' placeholder='height' /></div>

                    <div><span> Weight</span>
                        <input type="number" className='input' text='name' placeholder='weight' /></div>

                </form>
            </ModalNewProd>
        </div>
    )
}

export default ListProducts
